<script setup>
import { ref, onMounted, reactive } from 'vue';
import { useForm } from '@inertiajs/vue3';
import ActionMessage from '@/Components/ActionMessage.vue';
import InputError from '@/Components/InputError.vue';
import InputLabel from '@/Components/InputLabel.vue';
import Button from '@/Components/PrimaryButton.vue';
import AltButton from '@/Components/SecondaryButton.vue';
import TextInput from '@/Components/TextInput.vue';
import { useConfirm } from "primevue/useconfirm";
import ConfirmPopup from 'primevue/confirmpopup';
import { useI18n } from "vue-i18n";

import { useToast } from "primevue/usetoast";
import Toast from 'primevue/toast';
import lodash from 'lodash';
import deepdash from 'deepdash';    

const _ = deepdash(lodash);

const toast = useToast();
const confirm = useConfirm();

const {t, locale} = useI18n();


const _data = reactive({ 
    tipusArticle:null,
    colors:null,
    materials:null,
    });


onMounted(async () => {

    const response1 = await fetch("/tipusarticle/"+locale.value);
    _data.tipusArticle = await response1.json();

    const response7 = await fetch("/materials/"+locale.value);
    _data.materials = await response7.json();

    const response = await fetch("/colors/"+locale.value);
    _data.colors = await response.json();


});

const props = defineProps({
    index: Number,
    workData: Object
});

const emit = defineEmits(['deleteWork', 'cloneWork', 'editeWork'])

const txtArticle = (id) => {
    var txt = _.filter(_data.tipusArticle, { 'idTipusArticle' : id });  
    return txt[0]?.tipusArticle;
};

const txtMaterial = (id) => {
    var txt = _.filter(_data.materials, { 'idMaterial' : id });
    return txt[0]?.material;

};

const txtColor = (id) => {
    var txt = _.filter(_data.colors, { 'idColor' : id });
    return txt[0]?.color;
};


const deleteWork = (event, index) => {
    confirm.require({
        target: event.currentTarget,
        message: 'Quieres borrar el trabajo?',
        icon: 'pi pi-trash',
        acceptIcon: 'pi pi-check',
        rejectIcon: 'pi pi-times',
        acceptClass: 'p-button-danger text-xs',
        rejectClass: 'p-button-info text-xs',
        accept: () => {
            toast.add({ severity: 'info', summary: 'Confirmed', detail: 'You have accepted', life: 1000 });
            emit('deleteWork', index);
        },
        reject: () => {
            toast.add({ severity: 'error', summary: 'Rejected', detail: 'You have rejected', life: 1000 });
        }
    });
};

const editWork = (event, index) => {
    emit('editWork', index);
};

const cloneWork = (event, index) => {
    confirm.require({
        target: event.currentTarget,
        message: 'Quieres duplicar el trabajo?',
        icon: 'pi pi-copy',
        acceptIcon: 'pi pi-check',
        rejectIcon: 'pi pi-times',
        acceptClass: 'p-button-success text-xs',
        rejectClass: 'p-button-info text-xs',
        accept: () => {
            emit('cloneWork', index);
        },
        reject: () => {
            toast.add({ severity: 'error', summary: 'Rejected', detail: 'You have rejected', life: 3000 });
        }
    });
};

</script>

