            <div class="flex">
                <div @click="deleteWork($event, index)">
                    <i
                        class="pi pi-trash text-xl ml-3 hover:cursor-pointer hover:text-primary-500 text-slate-500 transition-colors"></i>
                </div>
                <div @click="editWork($event, index)">
                    <i
                        class="pi pi-pencil font-light text-xl ml-3 hover:cursor-pointer hover:text-primary-500 text-slate-500 transition-colors"></i>
                </div>
                <div @click="cloneWork($event, index)">
